def showfi_remove(gammaval, remove27_2_10, remove27_2_20, remove27_2_50, remove27_3_10, remove27_3_20, remove27_3_50):
    #gamma = 2.5  n = 120824
    #k=2, m=10  
    remove27_2_10 = remove27_2_10[2:11] 
    #k=2, m=20
    remove27_2_20 = remove27_2_20[2:21]
    #k=2, m=50
    remove27_2_50 = remove27_2_50[2:51]
    #k=3, m=10
    remove27_3_10 = remove27_3_10[3:11]
    #k=3, m=20
    remove27_3_20 = remove27_3_20[3:21]
    #k=3, m=50
    remove27_3_50 = remove27_3_50[3:51]
    
    y = [] 
    for i in range( 1, 101):
        y.append(context.power(i, dc.Decimal(-gammaval)))
  
    plt.yscale('log')
    plt.xscale('log')
    plt.ylabel('P(i)')
    plt.xlabel('i (degree)')

    line1, = plt.plot(range(1, 101), y, "--", label=r'$\gamma$' + '=%0.1f' % gammaval)
    line2, = plt.plot(range(2, 11), remove27_2_10, marker='*', markersize=7, label='k=2, m=10')
    line3, = plt.plot(range(2, 21), remove27_2_20, marker='^', markersize=7, label='k=2, m=20')
    line4, = plt.plot(range(2, 51), remove27_2_50, marker='v', markersize=7, label='k=2, m=50')
    line5, = plt.plot(range(3, 11), remove27_3_10, marker='.', markersize=7, label='k=3, m=10')
    line6, = plt.plot(range(3, 21), remove27_3_20, marker='o', markersize=7, label='k=3, m=20')
    line6, = plt.plot(range(3, 51), remove27_3_50, marker='s', markersize=7, label='k=3, m=50')
    plt.legend(loc=3, handler_map={line1: HandlerLine2D(numpoints=2)})
    plt.show()

